# --------------- Helpers that build all of the responses ----------------------

def build_speechlet_response(title, output, reprompt_text, should_end_session):
    return {
        'outputSpeech': {
            'type': 'PlainText',
            'text': output
        },
        'card': {
            'type': 'Simple',
            'title': "SessionSpeechlet - " + title,
            'content': "SessionSpeechlet - " + output
        },
        'reprompt': {
            'outputSpeech': {
                'type': 'PlainText',
                'text': reprompt_text
            }
        },
        'shouldEndSession': should_end_session
    }

def build_response(session_attributes, speechlet_response):
    return {
        'version': '1.0',
        'sessionAttributes': session_attributes,
        'response': speechlet_response
    }


# --------------- Functions that control the skill's behavior ------------------
def get_GetPlaceIntent_response(intent):
    session_attributes = {}
    card_title = "GetPlaceIntent"
    global placeIntent
    placeIntent = str(intent['slots']['place']['value'])
    for place in placeList:
        if place == placeIntent:
            global placeIndex
            placeIndex = placeList.index(placeIntent)
            pList = placeVisitList[placeIndex]
            global num
            num = random.randint(0,len(pList))
            speech_output ="here my suggetion of place to visit in "+ placeIntent +" ... " + pList[num] + " ...do you want me to tell a fact about this place"
            reprompt_text = "You never responded to the first test message. Sending another one."
            should_end_session = False
            return build_response(session_attributes, build_speechlet_response(
                card_title, speech_output, reprompt_text, should_end_session))
            
